#define _WINSOCK_DEPRECATED_NO_WARNINGS #define _CRT_SECURE_NO_WARNINGS  #include "shared.h" #include <winsock2.h> #include <ws2tcpip.h> #include <iostream>  #pragma comment(lib, "ws2_32.lib")  using namespace std;  int main() {     WSADATA wsa;     SOCKET s = INVALID_SOCKET;      // Inicializimi i Winsock     if (WSAStartup(MAKEWORD(2, 2), &wsa) != 0) {         cout << "WSAStartup failed\n";         return 1;     }      // Krijimi i socket-it UDP     s = socket(AF_INET, SOCK_DGRAM, IPPROTO_UDP);     if (s == INVALID_SOCKET) {         cout << "Socket creation failed. Error: " << WSAGetLastError() << "\n";         WSACleanup();         return 1;     }      cout << "UDP client initialized successfully.\n";      // Mbyllja e socket-it     closesocket(s);     WSACleanup();      return 0; } 